#include <unistd.h>
#include <fcntl.h>

int main(int argc, char *argv[]) { 
    char* important_config = "important_config";

    // save the important config to a file, ensuring it is not world-writable
	int fd = open(important_config, O_WRONLY | O_CREAT | O_EXCL, 0600);
	if (fd == -1) {
		perror("open");
		exit(1);
	}

	// write the important config to the file
	if (write(fd, "important config", 14) == -1) {
		perror("write");
		exit(1);
	}

	// close the file
	if (close(fd) == -1) {
		perror("close");
		exit(1);
	}

	// open the file for reading
	fd = open(important_config, O_RDONLY);
	if (fd == -1) {
		perror("open");
		exit(1);
	}

	// read the important config from the file
	char buf[15];
	if (read(fd, buf, 14) == -1) {
		perror("read");
		exit(1);
	}

	// close the file
	if (close(fd) == -1) {
		perror("close");
		exit(1);
	}

	// print the important config
	printf("%s\n", buf);

	// remove the important config file
	if (unlink(important_config) == -1) {
		perror("unlink");
		exit(1);
	}

	return 0;
}

