#include <string.h>

using namespace std;

struct person {
    char name[100];
    int status;
};

int main(int argc, char *argv[]) {
    // allocate a new person
    person *p = new person;
    // set status to 0
	p->status = 0;
    // set name to "John"
	strcpy(p->name, "John");
    // print name and status
	cout << "name: " << p->name << ", status: " << p->status << endl;
    // free the memory
	delete p;
    return 0;
}
```

