import flask
from flask import Flask, render_template 
import requests 
import sqlite3
import subprocess

from markupsafe import escape

app = Flask(__name__)

@app.route("/")
def index():
    return "<p>Hello, World!</p>"

# function which says hello to the user
@app.route("hello/<username>")
def hello(username):
    return "<p>Hello, {}!</p>".format(escape(username))

@app.route("/api/v1/get_data")
def get_data():
    conn = sqlite3.connect('data.db')
    c = conn.cursor()
    c.execute("SELECT * FROM data")
    data = c.fetchall()
    conn.close()
    return str(data)

@app.route("/api/v1/add_data", methods=['POST'])
def add_data():
    conn = sqlite3.connect('data.db')
    c = conn.cursor()
    c.execute("INSERT INTO data VALUES (?,?)", (flask.request.form['name'], flask.request.form['value']))
    conn.commit()
    conn.close()
    return "OK"

@app.route("/api/v1/delete_data", methods=['POST'])
def delete_data():
    conn = sqlite3.connect('data.db')
    c = conn.cursor()
    c.execute("DELETE FROM data WHERE name =?", (flask.request.form['name'],))
    conn.commit()
    conn.close()
    return "OK"

@app.route("/api/v1/update_data", methods=['POST'])
def update_data():
    conn = sqlite3.connect('data.db')
    c = conn.cursor()
    c.execute("UPDATE data SET value =? WHERE name =?", (flask.request.form['value'], flask.request.form['name']))
    conn.commit()
    conn.close()
    return "OK"

if __name__ == "__main__":
    app.run(host='0.0.0.0', port=5000, debug=True)

