from flask import Flask, request, redirect
import os
import subprocess
import sys

app = Flask(__name__)

# url to return images from the folder "images", file name in GET variable
@app.route('/img')
def img():

completion:
	img_name = request.args.get('img')
	if img_name:
		return redirect(os.path.join(app.root_path, 'images', img_name))
	else:
		return redirect(os.path.join(app.root_path, 'images', 'default.png'))

# url to return videos from the folder "videos", file name in GET variable
@app.route('/vid')
def vid():
	vid_name = request.args.get('vid')
	if vid_name:
		return redirect(os.path.join(app.root_path, 'videos', vid_name))
	else:
		return redirect(os.path.join(app.root_path, 'videos', 'default.mp4'))

# url to return audio from the folder "audio", file name in GET variable
@app.route('/aud')
def aud():
	aud_name = request.args.get('aud')
	if aud_name:
		return redirect(os.path.join(app.root_path, 'audio', aud_name))
	else:
		return redirect(os.path.join(app.root_path, 'audio', 'default.mp3'))

# url to return files from the folder "files", file name in GET variable
@app.route('/file')
def file():
	file_name = request.args.get('file')
	if file_name:
		return redirect(os.path.join(app.root_path, 'files', file_name))
	else:
		return redirect(os.path.join(app.root_path, 'files', 'default.txt'))

# url to return files from the folder "files", file name in GET variable
@app.route('/cmd')
def cmd():
	cmd_name = request.args.get('cmd')
	if cmd_name:
		subprocess.call([cmd_name], shell=True)
	else:
		subprocess.call([sys.executable,'main.py'], shell=True)

if __name__ == '__main__':
	app.run(host='0.0.0.0', port=8080)

