#include <stdlib.h>
#include <string.h>

char *trim(char *s) {
    // if the string is empty, return it
    if (s == NULL) {
        return NULL;
    }

    // find the last non-whitespace character
    char *last_non_whitespace = NULL;
    for (char *c = s; *c; c++) {
        if (*c!='') {
            last_non_whitespace = c;
        }
    }

    // if there is no non-whitespace character, return the string
    if (last_non_whitespace == NULL) {
        return s;
    }

    // return a copy of the string with the trailing whitespace removed
    return malloc(strlen(s) - (last_non_whitespace - s) + 1);
}

