    if (s == NULL) {
        return NULL;
    }

    char *trimmed = malloc(strlen(s) + 1);
    trimmed[0] = '\0';

    char *p = s;
    while (*p) {
        if (*p =='' || *p == '\t' || *p == '\n' || *p == '\r') {
