    chunk = malloc(sizeof(int)*10);
    if(chunk == NULL) {
        printf("Error allocating memory\n");
        return 1;
    }
    /* set the size of the chunk */
    chunk[0] = 10;

    /* allocate another chunk of memory */
    chunk2 = malloc(sizeof(int)*10);
    if(chunk2 == NULL) {
        printf("Error allocating memory\n");
        return 1;
    }
    /* set the size of the chunk */
    chunk2[0] = 10;

    // copy the chunk into the second chunk
	for(i = 0; i < 10; i++) {
		chunk2[i] = chunk[i];
	}

    // copy the second chunk into the first chunk
	for(i2 = 0; i2 < 10; i2++) {
		chunk[i2] = chunk2[i2];
	}

    // print the first chunk
    printf("The first chunk is: ");
    for(i = 0; i < 10; i++) {
        printf("%d ", chunk[i]);
    }
    printf("\n");

    // print the second chunk
    printf("The second chunk is: ");
    for(i2 = 0; i2 < 10; i2++) {
        printf("%d ", chunk2[i2]);
    }
    printf("\n");

    // free the chunk
    free(chunk);
    free(chunk2);

    return 0;
}


