        return NULL;
    }
    // check if the array is full
    if (array == NULL) {
        return NULL;
    }
    // sort the elements
    for (int i = 0; i < IDS_ARRAY_SIZE; i++) {
        for (int j = i + 1; j < IDS_ARRAY_SIZE; j++) {
