    int pipefd[2];
    if (pipe(pipefd)!= 0) {
        perror("pipe");
        exit(1);
    }

    // fork
    pid_t child_pid = fork();
    if (child_pid == -1) {
        perror("fork");
        exit(1);
    }

    // if the child process
    if (child_pid == 0) {
        // close the pipe
        close(pipefd[0]);

        // execute the command
        system(command);

        // close the pipe
        close(pipefd[1]);

        // exit
