## 3.4.2. 编写一个程序，使用管道将文件内容发送到另一个进程

```c
#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>
#include <fcntl.h>

int main(int argc, char *argv[]) {
    // get a directory name from the command line
    char *dirname = argv[1];

    // create a pipe
    int pipefd[2];
    if (pipe(pipefd) == -1) {
        perror("pipe");
        return 1;
    }

    // create a process to read the contents of the directory
    pid_t pid = fork();
    if (pid == -1) {
        perror("fork");
        return 1;
    }
    if (pid == 0) {
        // read the contents of the directory
        char cmd[256] = {0};
#ifdef __linux__
        sprintf(cmd, "ls -l %s", dirname);
#else
        sprintf(cmd, "dir /b /s %s", dirname);
#endif
        FILE *fp = popen(cmd, "r");
        if (fp == NULL) {
            perror("popen");
            return 1;
        }

        // write the contents of the pipe to the process
        int n = write(pipefd[1], fp, 1024);
        if (n == -1) {
            perror("write");
            return 1;
        }

        // close the file pointer
        fclose(fp);

        // close the pipe
        close(pipefd[0]);

        // exit the process
        exit(0);
    }

    // read the contents of the pipe
    char buf[1024] = {0};
    int n = read(pipefd[0], buf, 1024);
    if (n == -1) {
        perror("read");
        return 1;
    }

    // close the pipe
    close(pipefd[0]);

    // print the contents of the pipe
    printf("%s\n", buf);

    // exit the process
    exit(0);
}
