#include <stdlib.h>
#include <string.h>

int main(int argc, char *argv[]) {
    const char* message = "Hello";
    const char* buffer = "World";

    // compare the two strings
    if (strcmp(message, buffer) < 0) {
        printf("message is smaller than buffer\n");
