```c
#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main(int argc, char *argv[]) {
    const char* message = "Hello";
    char buffer[256];

    // copy the message to the buffer
    memcpy(buffer, message, strlen(message));

    // reverse the message
    for (int i = strlen(message) - 1; i >= 0; i--) {
        buffer[i] = message[i];
