	person *p = (person *)malloc(sizeof(person));
	p->status = 0;

    // allocate a new pointer to a function and set it to point to print_status
	void (*pf)(int) = print_status;

    // call the function
	(*pf)(p->status);

    // free the person
	free(p);

    return 0;
}

